{{JSON.stringify({
	variables:{
  "input": [
    {
      "status": "CREATED",
      "box": {
        "sizeDepthCm": parseFloat(DepthInput.text),
        "sizeWidthCm": parseFloat(WidthInput.text),
        "sizeHeightCm": parseFloat(HeightInput.text),
      },
				"isActive":true,
      "shipment": {
        "id": ListShipmentDetail.selectedRow.id
      }
    }
  ]
},
	query:`
		mutation addShipmentItem($input:[AddShipmentItemInput!]!){
  addShipmentItem (input:$input){ 
    numUids
  }
}
	`
})}}

