actionConfiguration.body:
{{JSON.stringify({
	variables:{

  "input": {
		filter :{
			id: GetOrderDetail.data.data.getOrder.id
		},
		set : {
			shipments: {
      dateTimeCreatedUTC: moment(),
      status: "CREATED",
      type: "OUTBOUND",
      warehouse: {"id": EquipmentSelector.selectedOptionValue},
      transportCompany: "AUSPOST"
    },
    shipFromPartner: {"id": listShipFromPartners.selectedItem.partner.id},
		}

  }

	},
	query:`
mutation UpdateOrder($input:UpdateOrderInput!){
  updateOrder(input:$input){
    order {
      id
    }
  }
}
	`
})}}

