actionConfiguration.body:
{{JSON.stringify({
	variables:{
  "input": ListOrders.selectedItem.id
},
	query:`
query GetOrder($input:ID!){
  getOrder(id:$input){
    id
		warehouse {
			id
		}
		status
		orderType
    createdDateTime
    shipFromPartner{
			id
      name
    }
    shipToPartner{
				id
      name
    }
		lines{
			material{
        code
        name
      }
      quantity
			packedQty
			stockOnHandQty
		}
		shipments {
			id
			transportCompanyShipmentId
			status
    	reference
    	items (filter:{isActive:true}){
      	id
				status
				weightKg
				trackingDetailsArticleId
        trackingDetailsBarcodeId
        transportCompanyItemId
        box {
					id
          sizeDepthCm
        	sizeWidthCm
          sizeHeightCm
        }
      }
		}
  }
}
	`
})}}

