actionConfiguration.body:
{{JSON.stringify({
	variables:{
  "input": EquipmentSelector.selectedOptionValue
},
	query:`
		query GetOrderByEquipment($input:ID!){
  getEquipment(id:$input){
    orders(filter:{not:{orderType:{eq:PURCHASE_ORDER}}}){
      id
		warehouse {
			id
		}
		status
		orderType
    createdDateTime
    shipFromPartner{
			id
      name
    }
    shipToPartner{
				id
      name
    }
		lines{
			material{
        code
        name
      }
      quantity
			packedQty
			stockOnHandQty
		}
    }
  }
}
	`
})}}

