{{JSON.stringify({
	variables:{
  	"warehouse": [EquipmentSelector.selectedOptionValue],
	},
	query:`
query GetShippingAccount($warehouse:[ID!]!){
  queryShippingAccount (filter:{isActive:true})@cascade{
    transportCompany
    partner {
      id
      companyCode
    }
    warehouse(filter:{id:$warehouse}) {
      name
    }
  }
}
	`
})}}

