actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: [
    {
      material: {code: selectAddItemsMaterial.selectedOptionLabel},
      materialUse: "Packed",
      owner: {id: GetOrderDetail.data.data.getOrder.shipFromPartner.id},
			//user: {id: GetUserSites.data.data.getUser.id},
      quantity: parseInt(inputCarrierQuantity.text),
      to: {
        shipment: {id: ListShipmentDetail.selectedRow.id},
        shipmentItem: {id: ShipmentItemList.selectedItem.id}
      },
			from: {
				carrier: {id: carrierSelectCarrier.selectedOptionValue}
			}
    }
  ],
	"input1": {
    "filter": {
      "id": ShipmentItemList.selectedItem.id
    },
    "set": {
      "material": {
        "code": selectAddItemsMaterial.selectedOptionLabel
      },
      "plannedQuantity": AddItemsTabs.getQuantity()
    }
  }
		},
    query: `
mutation ReceiveSerialised($input:[AddInventoryTransactionInput!]!,$input1:UpdateShipmentItemInput!) {
	addInventoryTransactions(input:$input){
    material{code},
    storageLocation{name},
    quantity
  },
	updateShipmentItem(input:$input1){
    numUids
  }
}
    `
})
}}

