actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: [
    {
      material: {code: AddItemsMaterialSelector.selectedOptionLabel},
			materialSubLot: {code: SerialNumberInput.text},
      materialUse: "Packed",
      owner: {id: ListOrders.selectedItem.shipFromPartner.id},
			//user: {id: GetUserSites.data.data.getUser.id},
      quantity: 1,
      to: {
        shipment: {id: ListShipmentDetail.selectedRow.id},
        shipmentItem: {id: ShipmentItemList.selectedItem.id}
      },
			from: {
				storageLocation: {name: ""},
				status: {code: ""}
			}
    }
  ]
		},
    query: `
mutation ReceiveSerialised($input:[AddInventoryTransactionInput!]!) {
	addInventoryTransactions(input:$input){
    material{code},
    storageLocation{name},
    quantity
  }
}
    `
})
}}

