actionConfiguration.body:
{{JSON.stringify({
	variables:{
  	"id": AddItemsMaterialSelector.selectedOptionValue,
		"warehouse": [EquipmentSelector.selectedOptionValue]
	},
	query:`
query GetInventoryHandlingPolicy($id:ID!, $warehouse:[ID!]!){
  getMaterialDefinition(id:$id) {
		serialMask
    inventoryHandlingPolicy {
      trackByCarrier
      trackBySerialNumber

      rules @cascade{
        warehouse (filter:{id:$warehouse}){
          name
        }
        stateModel {
          states (filter:{canPack:true}){
            status {
              label:code
							value:id
            }
          }
        }
      }
    }
  }
}
	`
})}}

