actionConfiguration.body:
{{JSON.stringify({
	variables:{
  	"shipment": ListOrders.selectedItem.id,
	},
	query:`
query QueryShipments($shipment:ID!){
  getOrder(id:$shipment){
		shipments {
			id
			transportCompanyShipmentId
			status
    	reference
    	items (filter:{isActive:true}){
      	id
				status
				weightKg
				trackingDetailsArticleId
        trackingDetailsBarcodeId
        transportCompanyItemId
        box {
					id
          sizeDepthCm
        	sizeWidthCm
          sizeHeightCm
        }
      }
		}
  }
}
	`
})}}

