actionConfiguration.body:
{{JSON.stringify({
	variables:{
  "input": {
    "filter": {
      "id": ShipmentItemList.selectedItem.box.id
    },
    "set": {
      "sizeWidthCm": parseInt(BoxWidthInput.text),
      "sizeDepthCm": parseInt(BoxLengthInput.text),
      "sizeHeightCm": parseInt(BoxHeightInput.text)
    }
  }
},
	query:`
		mutation updateBox($input:UpdateBoxInput!){
  updateBox(input:$input){
    numUids
  }
}
	`
})}}

