actionConfiguration.body:
{{JSON.stringify({
	variables:{
  "input1": {
    "filter": {
      "id": ShipmentItemList.selectedItem.id
    },
    "set": {
      "material": {
        "code": AddItemsMaterialSelector.selectedOptionLabel
      },
      "plannedQuantity": AddItemsTabs.getQuantity()
    }
  }
},
	query:`
		mutation updateBox($input1:UpdateShipmentItemInput!){
  updateShipmentItem(input:$input1){
    numUids
  }
}
	`
})}}

